import { Outlet } from "react-router-dom";

const Welcome = () => {
    const { loginSore } = useStore()
    const loginLink = "./";
    const regLink = "./"
    const [showbox, setBox] = useState('showBox')

    function showLog() {
        console.log('here')

    }

    const showBox = () => {
        setBox('hideBox')
        console.log('show login box')
    }


    return (
        <>
            <Header
                login={loginLink}
                reg={regLink}
                onShow={showLog}
            />
            <Login onShow ={showBox} showLog = {showbox}/>
            <Outlet />
        </>

    )

}

export default Welcome
